import styles from './index.scss';

export const Alert = ({ status }) => {
    if (status === 'warning') {
        return (
            <div className="alert">
                <div className="alert__logo">
                    <img src='https://img.icons8.com/?size=100&id=360&format=png&color=000000'></img>
                </div>
                <div className="alert__text">
                    Lorem, ipsum.
                </div>
                <div className="alert__close-btn">
                    <svg id="Icons" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="m12 23a11 11 0 1 0 -11-11 11.013 11.013 0 0 0 11 11zm0-20a9 9 0 1 1 -9 9 9.01 9.01 0 0 1 9-9z"/><path d="m8.293 15.707a1 1 0 0 0 1.414 0l2.293-2.293 2.293 2.293a1 1 0 0 0 1.414-1.414l-2.293-2.293 2.293-2.293a1 1 0 0 0 -1.414-1.414l-2.293 2.293-2.293-2.293a1 1 0 0 0 -1.414 1.414l2.293 2.293-2.293 2.293a1 1 0 0 0 0 1.414z"/></svg>
                </div>
            </div>
        )
    }
}
